// /client/src/components/Emails.js
import React, { useState } from 'react';

function Emails() {
  const [responseMsg, setResponseMsg] = useState('');

  const handleTriggerEmail = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/send-emails', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        }
      });
      const data = await response.json();
      setResponseMsg(data.message);
    } catch (error) {
      console.error('Error triggering email process:', error);
      setResponseMsg('Failed to trigger email sending.');
    }
  };

  return (
    <div>
      <h1>Send Emails</h1>
      <p>This is a placeholder for the email sending feature.</p>
      <button onClick={handleTriggerEmail}>Trigger Email Sending</button>
      {responseMsg && <p>{responseMsg}</p>}
    </div>
  );
}

export default Emails;
