// /client/src/components/Emails.js
import React, { useState } from 'react';

function Emails() {
  const [responseMsg, setResponseMsg] = useState('');

  const handleTriggerEmail = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/send-emails', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        }
      });
      const data = await response.json();
      setResponseMsg(data.message);
    } catch (error) {
      console.error('Error triggering email process:', error);
      setResponseMsg('Failed to trigger email sending.');
    }
  };

  return (
    <div>
      <h1>Send Emails</h1>
      <p>Our automated learning support system helps each student succeed by providing personalized study materials based on their test performance. After each assessment, the system analyzes how well students understood different topics and automatically sends them customized study resources via email. Rather than using a one-size-fits-all approach, students receive materials specifically chosen to match their current understanding of each topic. For example, if a student shows strong understanding in one area but needs more support in another, they'll receive different types of resources for each topic. This personalized approach ensures every student gets exactly the help they need to improve, delivered straight to their inbox. The system makes it easy for students to access relevant study materials without having to search through multiple resources or guess which materials might be most helpful for their situation.</p>
      <button onClick={handleTriggerEmail}>Send Emails!</button>
      {responseMsg && <p>{responseMsg}</p>}
    </div>
  );
}

export default Emails;
