import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Home from './components/Home';
import Dashboard from './components/Dashboard';
import Emails from './components/Emails';
import RagQuery from './components/RagQuery';

function App() {
  // Inline style for the navigation bar using Courier font
  const navStyle = {
    fontFamily: '"Courier New", Courier, monospace',
    display: 'flex',
    gap: '20px',
    padding: '20px',
    backgroundColor: '#1f1f1f'
  };

  // Inline style for the main container
  const containerStyle = {
    fontFamily: '"Courier New", Courier, monospace',
    padding: '20px'
  };

  return (
    <Router>
      <nav className="navbar" style={navStyle}>
        <Link to="/rag">Smart Analytics</Link>
        <Link to="/dashboard">Visual Insights</Link>
        <Link to="/emails">Automated Support</Link>
      </nav>
      <div className="container" style={containerStyle}>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/dashboard" element={<Dashboard />} />
          <Route path="/emails" element={<Emails />} />
          <Route path="/rag" element={<RagQuery />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
