import React from 'react';

function Home() {
  // Container style for centering content and applying the dark theme
  const containerStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: '40px 20px',
    textAlign: 'center',
    backgroundColor: '#121212',
    color: '#ffffff',
    minHeight: '100vh',
    fontFamily: 'Roboto, sans-serif' // Use Roboto font here
  };

  // Style for the main heading
  const headingStyle = {
    fontSize: '3rem',
    marginBottom: '20px'
  };

  // Style for sub-headings
  const subHeadingStyle = {
    fontSize: '1.5rem',
    margin: '10px 0'
  };

  // Style for section titles
  const sectionTitleStyle = {
    fontSize: '2rem',
    margin: '40px 0 15px'
  };

  // Style for paragraphs
  const paragraphStyle = {
    fontSize: '1.1rem',
    lineHeight: '1.6',
    maxWidth: '800px',
    margin: '0 auto 15px'
  };

  // Style for quotes
  const quoteStyle = {
    fontStyle: 'italic',
    fontSize: '1.2rem',
    margin: '20px 0'
  };

  // Style for quote authors
  const quoteAuthorStyle = {
    marginTop: '10px',
    fontWeight: 'bold'
  };

  return (
    <div style={containerStyle}>
      <h1 style={headingStyle}>Welcome to TeachSmart</h1>
      <p style={paragraphStyle}>
        Transform the way you support student success. TeachSmart gives you instant insights and automated personalization to help every student reach their potential.
      </p>
      <p style={{ ...paragraphStyle, fontWeight: 'bold', fontSize: '1.2rem' }}>
        Your Command Center for Student Achievement
      </p>

      <div style={{ marginTop: '40px', maxWidth: '800px' }}>
        <h2 style={sectionTitleStyle}>Smart Analytics</h2>
        <p style={paragraphStyle}>
          Ask natural questions about student performance and get instant, data-driven answers.
        </p>
        <h2 style={sectionTitleStyle}>Visual Insights</h2>
        <p style={paragraphStyle}>
          Track student progress through intuitive dashboards that highlight trends and patterns.
        </p>
        <h2 style={sectionTitleStyle}>Automated Support</h2>
        <p style={paragraphStyle}>
          Send personalized study materials based on each student's exam performance with just one click.
        </p>
      </div>

      <div style={{ marginTop: '60px', maxWidth: '800px' }}>
        <h2 style={sectionTitleStyle}>How It Works</h2>
        <p style={paragraphStyle}>
          <strong>Upload your student assessment data</strong>
        </p>
        <p style={paragraphStyle}>
          Get immediate insights through natural language questions or visual dashboards.
        </p>
        <p style={paragraphStyle}>
          Automatically generate and send customized study resources to students who need extra support.
        </p>
      </div>

      <div style={{ marginTop: '60px', maxWidth: '800px' }}>
        <h2 style={sectionTitleStyle}>Why Teachers Love TeachSmart</h2>
        <p style={quoteStyle}>
          "I used to spend hours analyzing exam results and creating individual study plans. TeachSmart does this in minutes, letting me focus on what matters most - teaching."
        </p>
        <p style={quoteAuthorStyle}>Sarah Chen, High School Mathematics</p>
      </div>
    </div>
  );
}

export default Home;
