import React from 'react';

function Home() {
  // Container style for centering content and applying the dark theme with Courier font
  const containerStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: '60px 20px',
    textAlign: 'center',
    backgroundColor: '#121212',
    color: '#ffffff',
    minHeight: '100vh',
    fontFamily: '"Courier New", Courier, monospace'
  };

  // Style for the main heading with a subtle text shadow
  const headingStyle = {
    fontSize: '3.5rem',
    marginBottom: '30px',
    letterSpacing: '1px',
    textShadow: '2px 2px 4px rgba(0, 0, 0, 0.5)'
  };

  // Style for sub-headings
  const subHeadingStyle = {
    fontSize: '1.5rem',
    margin: '15px 0'
  };

  // Style for section titles with an accent border for emphasis
  const sectionTitleStyle = {
    fontSize: '2.5rem',
    margin: '50px 0 20px',
    borderBottom: '2px solid #1e90ff',
    display: 'inline-block',
    paddingBottom: '10px'
  };

  // Style for paragraphs with increased line height for readability
  const paragraphStyle = {
    fontSize: '1.2rem',
    lineHeight: '1.8',
    maxWidth: '800px',
    margin: '0 auto 20px'
  };

  // Style for quotes with a border accent and extra padding
  const quoteStyle = {
    fontStyle: 'italic',
    fontSize: '1.4rem',
    margin: '30px 0',
    borderLeft: '4px solid #1e90ff',
    paddingLeft: '20px'
  };

  // Style for quote authors
  const quoteAuthorStyle = {
    marginTop: '10px',
    fontWeight: 'bold',
    fontSize: '1.2rem'
  };

  return (
    <div style={containerStyle}>
      <h1 style={headingStyle}>Welcome to TeachSmart</h1>
      <p style={paragraphStyle}>
        Transform the way you support student success. TeachSmart gives you instant insights and automated
        personalization to help every student reach their potential.
      </p>
      <div style={{ marginTop: '50px', maxWidth: '800px' }}>
        <h2 style={sectionTitleStyle}>Smart Analytics</h2>
        <p style={paragraphStyle}>
          Ask natural questions about student performance and get instant, data-driven answers.
        </p>
        <h2 style={sectionTitleStyle}>Visual Insights</h2>
        <p style={paragraphStyle}>
          Track student progress through intuitive dashboards that highlight trends and patterns.
        </p>
        <h2 style={sectionTitleStyle}>Automated Support</h2>
        <p style={paragraphStyle}>
          Send personalized study materials based on each student's exam performance with just one click.
        </p>
      </div>

      <div style={{ marginTop: '60px', maxWidth: '800px' }}>
        <h2 style={sectionTitleStyle}>How It Works</h2>
        <p style={paragraphStyle}>
          <strong>Upload your student assessment data</strong>
        </p>
        <p style={paragraphStyle}>
          Get immediate insights through natural language questions or visual dashboards.
        </p>
        <p style={paragraphStyle}>
          Automatically generate and send customized study resources to students who need extra support.
        </p>
      </div>

      <div style={{ marginTop: '60px', maxWidth: '800px' }}>
        <h2 style={sectionTitleStyle}>Why Teachers Love TeachSmart</h2>
        <p style={quoteStyle}>
          "I used to spend hours analyzing exam results and creating individual study plans. TeachSmart does this in
          minutes, letting me focus on what matters most - teaching."
        </p>
        <p style={quoteAuthorStyle}>Gayatri, High School Mathematics</p>
      </div>
    </div>
  );
}

export default Home;
